import React, { useState, useEffect } from "react";
import "../css/form.css";
import axios from "axios";
import { useParams } from "react-router";
import { Form, Button, Checkbox, DatePicker, Input, Select, Space } from "antd";
import 'moment/locale/zh-cn';
import moment from 'moment';
import locale from 'antd/es/date-picker/locale/zh_CN';
function FormUpdate() {
  const [form] = Form.useForm();
  const { studentId } = useParams();
  const [data, setData] = useState({});
  const [newStudent, setNewStudent] = useState({});

  useEffect(() => {
    // const newStudent ={};
    axios
      .get(`http://localhost:8888/api/student/${studentId}`)
      .then((res) => {
        console.log(res.data);
        setData(res.data);
      })
      .catch((error) => console.log(error));
  }, []);
  useEffect(() => {
    form.setFieldsValue({
      ...data,
    });
  }, [data, form]);

  // useEffect(() => {
  //   function handleSubmit() {
  //     axios
  //       .patch(`http://localhost:8888/api/student/${studentId}`, data)
  //       .then((res) => {
  //         console.log(res.data);
  //         setData(res.data);
  //       })
  //       .catch((error) => console.log(error));
  //   }
  //   handleSubmit();
  // }, []);

  return (
    <div>
      <div className="App">
        <header className="App-header">
         {data &&  <Form form={form}

            autoComplete="off"
            labelCol={{ span: 10 }}
            wrapperCol={{ span: 14 }}

            onFinish={(value) => {
              setData(value);
            }}
            onFinishFailed={(error) => {
              console.log({ error });
            }}

          >
            <Form.Item
              name="studentId"
              label="Student Id"

              rules={[
                {
                  required: true,
                  message: "Please enter your studentId",
                },
                { whitespace: true },
                { min: 3 },
              ]}
              hasFeedback
            >
              <Input placeholder="Type your studentId" />
            </Form.Item>

            <Form.Item
              name="name"
              label="Name"
              rules={[
                {
                  required: true,
                  message: "Please enter your Name",
                },
                { whitespace: true },
                { min: 3 },
              ]}
              hasFeedback
            >
              <Input placeholder="Type your Name" />
            </Form.Item>

            <Form.Item
              name="placeofbirth"
              label="PlaceOfBirth"
              rules={[
                {
                  required: true,
                  message: "Please enter your PlaceOfBirth",
                },
                { whitespace: true },
                { min: 3 },
              ]}
              hasFeedback
            >
              <Input placeholder="Type your PlaceOfBirth" />
            </Form.Item>

            <Form.Item name="male" label="Gender" requiredMark="optional">
              <Select placeholder="Select your gender" >
                <Select.Option value="male" name="male">Male</Select.Option>
                <Select.Option value="female">Female</Select.Option>
              </Select>
            </Form.Item>

            <Form.Item
              name="birthday"
              label="birthday"
              rules={[
                {
                  required: true,
                  message: "Please provide your birthday",
                },
              ]}
              hasFeedback
            >
              <DatePicker
                style={{ width: "100%" }}
                picker="date"

                defaultValue={moment('2015-01-01', 'YYYY-MM-DD')}
                placeholder="Chose date of birth"
              />
            </Form.Item>

            <Form.Item
              name="depname"
              label="Department"
              rules={[
                {
                  required: true,
                  message: "Please enter your department",
                },
                { whitespace: true },
                { min: 3 },
              ]}
              hasFeedback
            >
              <Input placeholder="Type your department" />
            </Form.Item>

            <Form.Item wrapperCol={{ span: 24 }}>
              <Button
                block
                style={{ margin: "8px" }}
                type="primary"
                htmlType="submit"
              >
                Update
              </Button>
              <Button block style={{ margin: "8px" }} type="button">
                Cancel
              </Button>
            </Form.Item>
          </Form>}
        </header>
      </div>
    </div>
  );
}

export default FormUpdate;
