import React,{useState,useEffect} from 'react'
import axios from 'axios';
import PropTypes from 'prop-types'
import './login.css'
function Login(props) {
    const [data,setData] = useState({});

    useEffect(() => {
        let name='chuotcon';
        let password="chuotcon";
        axios
          .get(`http://localhost:8888/api/forum/login?name=${name}&password=${password}`)
          .then((res) => {
            console.log(res.data)
            setData(res.data);
            
          })
          .catch((error) => console.log(error));
      }, []);
    return (
        <div className="container" style={{ border: "1px solid black", width: 350 }}>
            <div
                className="header"
                style={{ borderBottom: "1px solid black", backgroundColor: "aqua" }}
            >
                <p style={{ textAlign: "center", paddingTop: 5, paddingBottom: 5, fontSize: 20, fontWeight: "bold" }}> Đăng nhập</p>
            </div>
            <div className="body">
                <table style={{ marginBottom: 20 }}>
                    <form />
                    <tbody>
                        <tr>
                            <td>Tên Đăng Nhập</td>
                            <td>
                                <input type="text" name="userName"maxLength={10} required="" />
                            </td>
                        </tr>
                        <tr>
                            <td style={{ textAlign: "right" }}>Mật khẩu</td>
                            <td>
                                <input type="password" name="password" required="" />
                            </td>
                        </tr>
                        <tr>
                            <td />
                            <td>
                                <button type="submit">Đăng nhập</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        </div>

    )
}

Login.propTypes = {}

export default Login
