import React, { useEffect, useState } from "react";
import "../css/student.css";
import axios from "axios";
import { useParams } from "react-router";
function StudentDetail() {
  const {studentId} = useParams();
  const [data,setData] = useState({});
  useEffect(() => {
    axios
      .get(`http://localhost:8888/api/student/${studentId}`)
      .then((res) => {
        setData(res.data);
      })
      .catch((error) => console.log(error));
  }, []);
  // const location=useLocation();
  // const {studentId,name,birthday}=location.state;
  // console.log(studentId,name,birthday)
  return (
    <div className="studentDetailContainer">
      <h1>Student Detail</h1>
      <div className="studentDetail">
        <div className="studentDetailLeft">
          <div>StudentID</div>
          <div>Name</div>
          <div>Male</div>
          <div>BirthDay</div>
          <div>PlaceOfBirth</div>
          <div>Address</div>
          <div>Department</div>
        </div>
        <div className="studentDetailRight">
          <div className="studentId">{data.studentId}</div>
          <div className="name">{data.name}</div>
          <input type="checkbox" />
          <div className="birthDay">{data.birthday}</div>
          <div className="placeOfBirth">{data}</div>
          <div className="address">45 ký con</div>
          <div className="department">Anh Văn</div>
        </div>
        <div></div>
      </div>
      <div>
        <button>Student List</button>
        <button>Edit...</button>
      </div>
    </div>
  );
}

export default StudentDetail;
